def plotPComparison(xdata,ydata):
    plt.figure(2,figsize=(10,8))
    ax=plt.axes()
    ax.set_ylabel('P [MPa]',fontsize='x-large')
    ax.set_xlabel('θ[°]',fontsize='x-large',loc='right')
    plt.xticks(fontsize='x-large')
    plt.yticks(fontsize='x-large')
    plt.plot(x,y,'x',label='Experimental')
    plt.plot(xdata,ydata,color='black',linewidth=2.5, label='Simulação')
    plt.legend(fontsize='x-large')
    

def plotSimulation(xdata,ydata):
    plt.figure(2,figsize=(10,8))
    ax=plt.axes()
    ax.set_ylabel('T [K]',fontsize='x-large')
    ax.set_xlabel('θ[°]',fontsize='x-large',loc='right')
    plt.xticks(fontsize='x-large')
    plt.yticks(fontsize='x-large')
    
    plt.plot(xdata,ydata,color='black',linewidth=2.5)
    plt.legend(fontsize='x-large')